$.fn.setVgwortPdfLink=function(){var c=/^(.*\.met\.vgwort\.de\/na\/[a-z0-9]{30,32})\?l=(.*)/i;$(this).each(function(){var a=$(this),b=c.exec(a.prop("href"));null!==b&&a.attr("href",b[2]).click(function(){var a=b[1];$(document.body).append("<img src='"+a+"' width='1' height='1' alt=''>")})});return this};